mapper.select:
SELECT COUNT(*)
 	  FROM MEMBER
 WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE CERT
   SET CERT_NO = #{certNo}, CERT_TIME= SYSDATE   
 WHERE EMAIL = #{email}

UPDATE MEMBER
SET MEMBER_PWD = #{memberPwd}
 WHERE MEMBER_ID = #{memberId}
   AND MEMBER_STATUS = 'N'

	UPDATE MEMBER
	SET MEMBER_PWD = #{memberPwd}
	 WHERE MEMBER_ID = #{memberId} AND MEMBER_NAME = #{memberName} AND 
	     MEMBER_PHONE = #{memberPhone} AND MEMBER_EMAIL = #{memberEmail}
     AND MEMBER_STATUS = 'N'
